import React , { useState , useEffect}from 'react'
import axios from 'axios'


export default function Cmt() {
    const [games,setGames] = useState([])

    useEffect(() => {
        axios.get('/game')
        .then((res) => {
            // setGames((prev)=>[...prev,res.data]);
          setGames(res.data);
        })
        .catch((err) => {
            console.log(err);
        })
    },[]);
    console.log(games[1]);
    return (
        <div>
            <ul>
                {
                    games.map(game=>{
                        <li key={game.id}>{game.title}</li>
                    })
                }
            </ul>
        </div>
    )
}
