import React from 'react';

import "./famous-game.css";
import 'bootstrap/dist/css/bootstrap.min.css';

import InfiniteCarousel from 'react-leaf-carousel';
import GameCell from '../GameCell/game-cell';


function FamousGame({listGame}) {
    console.log(listGame);
    return (
        <div>
            <h3>FAMOUS GAME</h3>
            <InfiniteCarousel 
                breakpoints={[
                {
                    breakpoint: 500,
                    settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    },
                },
                {
                    breakpoint: 768,
                    settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    },
                },
                ]}
                dots={true}
                showSides={true}
                sidesOpacity={0.5}
                sideSize={0.1}
                slidesToScroll={4}
                slidesToShow={4}
                scrollOnDevice={true}
            >
                {/* <div>
                    {listGame.map(game => (
                        <div key={game.id}>
                            <a className="famous-game-link" href={'/game-detail/' + game.Url}>
                                <img className="famous-game__img" src='' alt="Cannot get image" />
                                <div className="famous-game__name">{game.Title}</div>
                                <div className="famous-game__dev">Arcade games</div>
                            </a>
                        </div>
                    ))
                    }
                </div> */}
                
            </InfiniteCarousel>
                <div className="carousel-inner">
                    <div className="carousel-item active">
                    <img className="d-block w-100" src="..." alt="First slide"/>
                    </div>
                    <div className="carousel-item">
                    <img className="d-block w-100" src="..." alt="Second slide"/>
                    </div>
                    <div className="carousel-item">
                    <img className="d-block w-100" src="..." alt="Third slide"/>
                    </div>
                </div>
                <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="sr-only">Previous</span>
                </a>
                <a className="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="sr-only">Next</span>
                </a>
                </div>
           
    );
};
export default FamousGame;
