import React from 'react';

import "./famous-game.css";
import '../../../../node_modules/bootstrap/dist/css/bootstrap.min.css';

import InfiniteCarousel from 'react-leaf-carousel';
import GameCell from '../GameCell/game-cell';


function FamousGame({listGame}) {
    console.log(listGame);
    return (
        <div>
            <h3>FAMOUS GAME</h3>
            <InfiniteCarousel 
                breakpoints={[
                {
                    breakpoint: 500,
                    settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    },
                },
                {
                    breakpoint: 768,
                    settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    },
                },
                ]}
                dots={true}
                showSides={true}
                sidesOpacity={0.5}
                sideSize={0.1}
                slidesToScroll={4}
                slidesToShow={4}
                scrollOnDevice={true}
            >
                {/* <div>
                    {listGame.map(game => (
                        <div key={game.id}>
                            <a className="famous-game-link" href={'/game-detail/' + game.Url}>
                                <img className="famous-game__img" src='' alt="Cannot get image" />
                                <div className="famous-game__name">{game.Title}</div>
                                <div className="famous-game__dev">Arcade games</div>
                            </a>
                        </div>
                    ))
                    }
                </div> */}
                
            </InfiniteCarousel>
            <div id="carouselExampleSlidesOnly" class="carousel slide" data-ride="carousel">
                <div className="carousel-inner">
                    <div className="carousel-item active">
                    <img className="d-block w-100" src="..." alt="First slide"/>
                    </div>
                    <div className="carousel-item">
                    <img className="d-block w-100" src="..." alt="Second slide"/>
                    </div>
                    <div className="carousel-item">
                    <img className="d-block w-100" src="..." alt="Third slide"/>
                    </div>
                </div>
                </div>
        </div>
           
    );
};
export default FamousGame;
