import React from "react";

import './user-profile.css'

import {
  Button,
  Card,
  CardHeader,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

function UserProfile({user, setShowChangePassword}){

  // setName= (event) => {
  //   this.setState({Full_name: event.target.value});
  // }
  // setGender= (event) => {
  //   this.setState({Gender: event.target.value});
  // }
  // setDate= (event) => {
  //   this.setState({Date: event.target.value});
  // }
  // submitSave = () =>{
  //   console.log(this.state);
  // }
  // componentDidMount() {
  //   axios.get('/user')
  //        .then(res => {
  //           const user = res.data;
  //           this.setState({ user: user.user });
  //         })
  //        .catch(error => console.log(error));
  // };
    return(user)? (
      <>
        <div className="content">
          <Row>
            <Col md="8">
              <Card>
                <CardHeader>
                  <h1 className="title">Profile</h1>
                </CardHeader>
                  <div className="row Card-space" >
                  
                  <div className="col-md-3 " key = {user.id}  >
                    <h5>Full Name:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <form>
                      <input type="text" name="fullname" className="o" value={user.Full_name} /> 
                    </form>
                  </div>
                  </div>
                  <hr />
                  <div className="row">
                  <div className="col-md-3">
                    <h5>Sex:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <input type="radio" name="gender" className="a" id='gender_Male' checked="checked"   /> Male
                    <input type="radio" name="gender" className="a" id='gender_Female'checked="checked"  /> Female
                  </div>
                  </div>
                  <hr />
                  <div className="row">
                  <div className="col-md-3">
                    <h5>Date of birth:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <input type="date" name="bday" className="o"   />
                  </div>
                  </div>
                  <hr />
                  <div className="row ">
                  <div className="col-md-3">
                    <h5>Email:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <form>
                      <input type="text" name="email" className="o"  value={user.Email}/>
                    </form>
                  </div>
                </div>
                <hr />
                <div className="row">
                  <div className="col-md-3">
                    <h5>Password:</h5>
                  </div>
                  <div className="col-md-3 text-secondary"  >
                    <p>*************************</p>
                  </div>
                </div>
            
                <CardFooter>
                  <Button className="btn btn-fill"
                  >
                    Save
                  </Button>
                  <Button 
                    className="btn btn-fill"
                    onClick={() => setShowChangePassword(true)}
                  >
                    Change Password
                  </Button>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    ):null;
}

export default UserProfile;
