function SideBar(props) {
  
  const getAvatar = () => {
    axios.get('/uploads/images/users/' ).then(res => {
      console.log(res);
    });
  }
  const { routes } = props;
  var showImg = function(event) {
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(event.target.files[0]);
      };
  return(
    <BackgroundColorContext.Consumer>
      {({ color }) => (
        <div className="sidebar" data={color}>
          <div className="sidebar-wrapper"  > 
              <div className="logo">
                <form runat="server">
                  <img className="avatar-img" src="https://icon-library.com/images/avatar-icon-images/avatar-icon-images-4.jpg" alt=""   id="output"  width="200px" height="200px" />            
                  <input accept="image/*" className="inputImg" type='file' onChange={showImg}/>
                </form>
                
              <button onClick={getAvatar}>aaaaaa</button>
              </div>
            <Nav>
              {routes.map((prop) => {
                return (
                  <li 
                  >
                    <NavLink
                      to={prop.layout + prop.path}
                      className="nav-link"
                      activeClassName="active"
                      onClick={props.toggleSidebar}
                    >
                      <i className={prop.icon} />
                      <p>{prop.name}</p>
                    </NavLink>
                  </li>
                );
              })}
            </Nav>
          </div>
        </div>
      )}
    </BackgroundColorContext.Consumer>
  ) ;
}
export default SideBar;
