import React from "react";
import { Component } from "react";
import './sidebar.css'

import { NavLink } from "react-router-dom";
import { Nav } from "reactstrap";
import {
  BackgroundColorContext
} from "../BackgroundColorContext";

class Sidebar extends Component{
  constructor(props){
    super(props);
    this.state ={
      user : props.user
    }
  }
  sidebarRef = React.useRef(null);
  showImg = function(event) {
    var output = document.getElementById('output');
    output.src = URL.createObjectURL(event.target.files[0]);
  };
  render(){
    return(this.state.user ? (
      <BackgroundColorContext.Consumer>
        {({ color }) => (
          <div className="sidebar" data={color}>
            <div className="sidebar-wrapper" ref={sidebarRef} key ={this.state.user.id}>
                <div className="logo">
                  <form runat="server">
                    <img className="avatar-img" src={this.state.user.Avatar} alt=""   id="output"  width="200px" height="200px" />            
                    <input accept="image/*" className="inputImg" type='file' onChange={showImg}/>
                  </form>
                  <p className="username">{this.state.user.Full_name}</p>
                </div>
              <Nav>
                {routes.map((prop) => {
                  if (prop.redirect) return null;
                  return (
                    <li 
                    >
                      <NavLink
                        to={prop.layout + prop.path}
                        className="nav-link"
                        activeClassName="active"
                        onClick={props.toggleSidebar}
                      >
                        <i className={prop.icon} />
                        <p>{rtlActive ? prop.rtlName : prop.name}</p>
                      </NavLink>
                    </li>
                  );
                })}
              </Nav>
            </div>
          </div>
        )}
      </BackgroundColorContext.Consumer>
    ):null );
  }
}
function SideBar(props) {
 
  // const { routes, rtlActive } = props;
 
  
}


export default SideBar;
