import React from "react";
import axios from "axios";
import { Button } from 'semantic-ui-react'
import {Link, } from 'react-router-dom';


function ChooseCategories({categories,game}){
    const gameAvaUrl = axios.defaults.baseURL + 'uploads/images/games/avatar/' + game.Url;

    return (
        <div>
            <h3> {categories.CategoryName} </h3>
            <a className="categories-type-link">
                <img className="categories-type__img" src={categoriesAvaUrl} alt="Cannot get categories image" />
                        <div className="categories-type__name">{game.Title}</div>
            </a>
        </div>
    )}
export default ChooseCategories
