import React from 'react'
// import axios from 'axios'
import { useState}from 'react'
import "./GameType.css";
import CategoriesCell from '../CategoriesCell/CategoriesCell';
/* <!--Game Picture --> */

/* <!--Game Link --> */

function GameCategories({listCategory}){
 
    const allCategories = listCategory.map(categories => {
        return (
            <CategoriesCell categories={categories}/>
        );
    })
    
    const elementGame = document.querySelectorAll('.all-games')
    const filterBtn = document.querySelectorAll('#CategoryCell .game-type')
    console.log(Array.from(filterBtn))
    Array.from(filterBtn).forEach(function (element) {
        element.addEventListener('click', function (e) {
            // console.log(e.path[1]);
            for(let i=0; i<filterBtn.length; i++) {
                filterBtn[i].classList.remove('game-type--active');
                // e.path[i].classList.remove('game-type--active')
            }
            e.path[1].classList.add('game-type--active');
            
            let name_filter = element.dataset.filter;

        Array.from(elementGame).forEach(function(item){
            if (item.dataset.item.indexOf(name_filter) !== -1 || name_filter === 'all')
            item.style.display = 'block';
            else {
                item.style.display = 'none';
            }
        })
        // e.stopPropagation()
        })
    })

    return (
        <div style = {{marginLeft: "80px"}}>
            <h3>Game Type</h3>
                <div className = "CategoryCell" id="CategoryCell" >
                    {allCategories}
                </div>

        </div>
    );
};

export default GameCategories;
