import "./SortByCategory.css";
import React, {useState,useRef,useEffect} from 'react';
import axios from "axios";

/* <!--Game Picture --> */

/* <!--Game Link --> */

function SortByCategory({listCategory,listGame}){
const gameAvaUrl = axios.defaults.baseURL + 'uploads/images/games/avatar/' + listGame.Avatar;
const [categories,setlistCategory] = useState('')
const [game,setlistGame] = useState('')

useEffect(() => {
    axios.get('/games/id/' + game.Category).then((res)=>{
        console.log(res.data);
        setlistGame(res.data.game)
    })
    .catch((err) => {
        console.log(err);
    })
    axios.get('/categories/id/' + categories.CategoryName)
    .then((res)=>{
        console.log(res.data);
        setlistCategory(res.data.CategoryName)
    })
    .catch((err) => {
        console.log(err);
    })
},[])

    return(
        <div>
              <a className="famous-game-link" href={'/game-detail/' + game.Url}>
                <img className="famous-game__img" src={gameAvaUrl} alt="Cannot get image" />
                <div className="famous-game__name">{game.Title}</div>
                <div className="famous-game__dev">Arcade games</div>
            </a>
        
        </div>
    );
}
export default SortByCategory;
