import React from "react";
import './form.css'

export const Login = ({showLogin, setShowLogin, showSignUp, setShowSignUp}) => {
    const handleSubmit = e => {
        e.preventDefault();
    }; 
    
    return (showLogin & !showSignUp) ? (
        <form onSubmit={handleSubmit} className="popup-form" id="login-form">
            <div className="auth-inner">
                <label class="close-btn" onClick={() => setShowLogin(false)}>&times;</label>

                <h3>Login</h3>

                <div className="form-group">
                    <label className="form-label">Email:</label>
                    <input type="email" className="form-control" placeholder="Email"/>
                </div>

                <div className="form-group">
                    <label className="form-label">Password:</label>
                    <input type="password" className="form-control" placeholder="Password"/>
                </div>

                <div className="form-group">
                    <button className="btn btn-primary btn-block">Login</button>
                </div>
                
                <p>You don't have any account? <a href="/">Sign up now</a></p>    
                
                <a href="/" className = "forgot-password text-center">Forgot password?</a>
            </div>
        </form>
    ) : "";
}

export default Login;
