import {
  Button,
  Card,
  CardHeader,
  CardFooter,
  Row,
  Col,
} from "reactstrap";
import { Component } from "react";

class UserProfile extends Component{
  constructor(props){
    super(props);
    this.state = {
      isDisplayFormChangePass:false,
      user: props.user
      // id:'',
      // Full_name : '',
      // Gender:true,
      // Date : ''
    }
  }
  componentDidMount() {
    axios.get('/user' + user.id)
         .then(res => {
            const user = res.data;
            this.setState({ user: user.user });
          })
         .catch(error => console.log(error));
  };
