  //           this.setState({ user: user.user });
  //         })
  //        .catch(error => console.log(error));
  // };


  // handleEditSubmit = (event) => {
  //   event.preventDefault();
  //   const newUpdate = {
  //     id: this.state.id,
  //     Full_name: this.state.Full_name,
  //     Gender: this.state.Gender,
  //     Date: this.state.Date
  // };

  // axios.patch('/user/' , newUpdate)
