import React, {useRef} from "react";
import './form.css'
export const Login = ({showLogin, setShowLogin, setShowForgotPassword, setShowSignUp}) => {
    const handleSubmit = e => {
        e.preventDefault();
    }; 

    const formRef = useRef();

    const closeLogin = e => {
        if (formRef.current == e.target){
            setShowLogin(false)
        }
    }
    
    return (showLogin) ? (
        <div className="form-wrapper" ref={formRef} onClick={closeLogin}>
            <form onSubmit={handleSubmit} className="popup-form" id="login-form">
                <div className="auth-inner">
                    <label class="close-btn" onClick={() => setShowLogin(false)}>&times;</label>

                    <h3>Login</h3>

                    <div className="form-group">
                        <label className="form-label">Email:</label>
                        <input type="email" className="form-control" placeholder="Email"/>
                    </div>

                    <div className="form-group">
                        <label className="form-label">Password:</label>
                        <input type="password" className="form-control" placeholder="Password"/>
                    </div>

                    <div className="form-group">
                        <button className="btn btn-primary btn-block">Login</button>
                    </div>
                    
                    <p>You don't have any account? 
                        <label className="form-link"
                            onClick={() => {
                                setShowLogin(false)
                                setShowSignUp(true)
                            }}
                        >Sign up now</label></p>    
                    
                    <label className = "form-link text-center" 
                        onClick={() => {
                            setShowLogin(false);
                            setShowForgotPassword(true);
                        }}>Forgot password?
                    </label>
                </div>
            </form>
        </div>
    ) : null;
}

export default Login;
