import { useState, useRef, useEffect } from "react";

const PopupControl = () => {
    const [showLogin, setShowLogin] = useState(false)
    const [showSignUp, setShowSignUp] = useState(false)
    const [showForgotPassword, setShowForgotPassword] = useState(false)

    const HandlePopup = e => {useEffect(() => {
        const {name} = e.target
        console.log(name)
        if (name === "showLogin"){
            setShowLogin(true)
            setShowSignUp(false)
            setShowForgotPassword(false)
        }
        if (name === "showSignUp"){
            setShowLogin(false)
            setShowSignUp(true)
            setShowForgotPassword(false)
            console.log(setShowSignUp)
        }
        if (name === "showForgotPassword"){
            setShowLogin(false)
            setShowSignUp(false)
            setShowForgotPassword(true)
        }
    })}

    const btnCloseClick = () => {
        setShowLogin(false)
        setShowSignUp(false)
        setShowForgotPassword(false)
    }

    const formRef = useRef();
    const closeForm = e => {
        if (formRef.current === e.target){
            setShowLogin(false)
            setShowSignUp(false)
            setShowForgotPassword(false)
        }
    }

    return {showLogin, showSignUp, showForgotPassword, HandlePopup, btnCloseClick, formRef, closeForm}
}

export default PopupControl;
