import React from "react";
import { Route, Switch } from "react-router-dom";
import SideBar from "./SideBar/sidebar";
import routes from "./user-account-routes";

import { BackgroundColorContext } from "./BackgroundColorContext";
import UserProfile from "./UserProfile/user-profile";
import ListFriends from "../HomePage/FriendList/friend-list";
import Records from "./PersonalRecord/personal-record";
import PlayedGames from "./PlayedGames/played-games";

function UserAccount({user})  {
 
  const mainPanelRef = React.useRef(null);
  const getRoutes = () => {
    return () =>{
      
      <Route path={'/user-account/user-profile' + user.id} component={UserProfile} icon="tim-icons icon-single-02" name= "User Profile" layout= "/user-account" />
      {/* <Route path={'/user-account/records' + user.id} component={Records} icon="tim-icons icon-puzzle-10" name= "Records games" layout= "/user-account" />
      <Route path={'/user-account/friends-list' + user.id} component={ListFriends} icon="tim-icons icon-align-center" name= "Friends list" layout= "/user-account" />
      <Route path={'/user-account/played-games' + user.id} component={PlayedGames} icon="tim-icons icon-atom" name= "Played games" layout= "/user-account" /> */}
      
    }
    // return routes.map((prop, key) => {
    //   if (prop.layout === "/user-account") {
    //     return (
    //       <Route
    //         path={prop.layout + prop.path}
    //         component={prop.component}
    //         key={key}
    //         user = {user}
    //       />
    //     );
    //   } else {
    //     return null;
    //   }
    // });
  };
  return (
    <BackgroundColorContext.Consumer>
      {({ color }) => (
        <React.Fragment>
          <div className="wrapper">
            <SideBar
              routes={routes}
              user={user}
            />
            <div className="main-panel" ref={mainPanelRef} data={color} >
          
                {getRoutes()}
              
            </div>
          </div>
        </React.Fragment>
      )}
    </BackgroundColorContext.Consumer>

  );
}

export default UserAccount;
