import React from "react";
import "./CategoriesCell.css"
import "../GameCell/game-cell"
import classNames from 'classnames';


function CategoriesCell({categories}){
    const [hidden, setHidden] = useState(false);

    const handleClick = () => {
        setHidden(!hidden);
      };
    return (
            <div className=" game-type" className={classNames({hidden:hidden})} onClick={handleClick} data-filter={categories.CategoryName}>
                <a className="game-type-link">
                    {categories.CategoryName}
                </a>
            </div>
    );
}

export default CategoriesCell;
