import React, {useState} from "react";
import './friend-list.css'
import AddFriend from './add-friend.jsx'
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardBody,
  Table,
  Row,
  Col,
} from "reactstrap";


function ListFriend({user}){
  const [listFriend, setListFriend] = useState([]);
  const config = {
    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
};
  const getFriend = () => {
    axios.get('/friend/get-by-user-id/' + user.id )
        .then(res => {
            console.log(res.data.post);
            setListFriend(res.data.post)
        })
        .catch(err => {
            console.log(err);
        })
    }
  const showFullInfomationFriend = () =>{
    axios.post('/friend', {
      UserID : user.id,
      FullName : user.Full_name,
      GenDer : user.Gender,
      DateOfBirth : user.DayOfBirth,
      FriendEmail : user.Email
      }, config)
      .then(response => {
          getFriend()
      })
  }
    return  (user) ? (
    <>
    <div className="content">
      <Row>
        <Col className="12">
          <Card>
          <CardHeader>
            <h1 className="title">List friend</h1>
            <button 
              className="btnAddFr"
            >      
            <i className="fas fa-user-plus"></i>
            </button>
          </CardHeader>
          <CardBody>
            <Table className="tablesorter" responsive>
            <thead className="text-primary">
                      <tr>
                        <th>Full Name</th>
                        <th>Sex</th>
                        <th>Day of birth</th>
                        <th>Email</th>
                      </tr>
            </thead>
                    <tbody>
                    {
                      listFriend ? listFriend.map((friend) => (
                      <tr key={friend.id}>
                      <td>{friend.FullName}</td>
                      <td>{friend.GenDer}</td>
                      <td>{friend.DateOfBirth}</td>
                      <td>{friend.FriendEmail}</td>
                    </tr>
                      ))
                      :null
                    }
                    </tbody>
            </Table>
          </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
    </>
  ) : null;
}

export default ListFriend;
