import "./SortByCategory.css";
import React,{useState} from 'react';
import axios from "axios";

/* <!--Game Picture --> */

/* <!--Game Link --> */

function SortByCategory({listCategory,listGame,state}){
const gameAvaUrl = axios.defaults.baseURL + 'uploads/images/games/avatar/' + listGame.Avatar;

 const {categories,game} = {listCategory, listGame} = this.state;
const Sort = this.state.info.map((categories, game) =>
    <game-cell key={game} title={game.title} opened={game.opened}
                  onClick={() => this.toggleInfo(game)}>
        {game.filter((item) => item.categories == game.key).map(({item}) => ( 
                <h2 >{item.game}</h2>
        ))}
    </game-cell>
);
}
export default SortByCategory;
