import './friend-list.css'
import AddFriend from './add-friend.jsx'
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardBody,
  Table,
  Row,
  Col,
} from "reactstrap";


function ListFriend({user}){
  const  isDisplayForm = false;
  const OpenForm = () =>{
      this.setState({
        isDisplayForm : !this.state.isDisplayForm
      });
  }
  const onCloseForm =() =>{
    this.setState({
      isDisplayForm : !this.state.isDisplayForm,
      user:[]
    });
  }
    return  (user) ? (
    <>
    <div className="content">
      <Row>
        <Col className="12">
          <Card>
          <CardHeader>
            <h1 className="title">List friend</h1>
            <button 
              className="btnAddFr"
            >      
            <i className="fas fa-user-plus"></i>
            </button>
          </CardHeader>
          <CardBody>
            <Table className="tablesorter" responsive>
            <thead className="text-primary">
                      <tr>
                        <th>Full Name</th>
                        <th>Sex</th>
                        <th>Day of birth</th>
                        <th>Email</th>
                      </tr>
            </thead>
            
            
                    <tbody>
                      <tr >
                      <td></td>
                      <td></td>
                      <td></td>
                      <td></td>
                    </tr>
                    </tbody>
                   
            
            </Table>
          </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
    </>
  ) : null;
}

export default ListFriend;
