export default function Validator(values){
    let errors = {}

    // Full name
    if (values.full_name.trim()){
        errors.full_name = "Enter your name!"
    }

    // Email
    if (values.email.trim()){
        errors.full_name = "Email is required!"
    }
    else if (!/^[A-Z0-9._%+-]+\.[A-Z]{2,}$/i.test(values.email)){
        errors = "Email is not valid!"
    }

    // Password
    if (!values.password){
        errors.password = "Password is required!"
    }

    // Confirm password
    if (!values.confirm_password){
        errors.confirm_password = "Confirm password is required!"
    }
    else if (values.confirm_password !== values.password){
        errors.confirm_password = "Confirm password is not match!"
    }

    


}
