import { useState, useRef } from "react";

const popupControl = () => {
    const [showLogin, setShowLogin] = UseState(false)
    const [showSignUp, setShowSignUp] = UseState(false)
    const [showForgotPassword, setShowForgotPassword] = UseState(false)


    const popupLogin = () => {
        setShowLogin(true)
        setShowSignUp(false)
        setShowForgotPassword(false)
    }

    const popupSignUp = () => {
        setShowLogin(false)
        setShowSignUp(true)
        setShowForgotPassword(false)
    }

    const popupForgotPassword = () => {
        setShowLogin(false)
        setShowSignUp(false)
        setShowForgotPassword(true)
    }

    const btnCloseClick = () => {
        setShowLogin(false)
        setShowSignUp(false)
        setShowForgotPassword(false)
    }

    const formRef = useRef();
    const closeForm = e => {
        if (formRef.current === e.target){
            setShowLogin(false)
            setShowSignUp(false)
            setShowForgotPassword(false)
        }
    }

    return {showLogin, showSignUp, showForgotPassword, popupLogin, 
        popupSignUp, popupForgotPassword, btnCloseClick, formRef, closeForm}
}

export default popupControl;
