// import axios from 'axios'
// import React, { useEffect , useState , useRef }from 'react'
import "./GameType.css";
import CategoriesCell from '../CategoriesCell/CategoriesCell';
import InfiniteCarousel from 'react-leaf-carousel';
/* <!--Game Picture --> */

/* <!--Game Link --> */

function GameCategories({listCategory,listGame}){
    

    listCategory.map(categories => {})
    listGame.map(game => {})

    const gameAvaUrl = axios.defaults.baseURL + 'uploads/images/games/avatar/' + game.Avatar;
    return (
        <div>
            <h3>Game Type</h3>
            {/* <InfiniteCarousel 
                    breakpoints={[
                    {
                        breakpoint: 500,
                        settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2,
                        },
                    },
                    {
                        breakpoint: 768,
                        settings: {
                        slidesToShow: 3,
                        slidesToScroll: 3,
                        },
                    },
                    ]}
                    dots={true}
                    showSides={true}
                    sidesOpacity={0.5}
                    sideSize={0.1}
                    slidesToScroll={4}
                    slidesToShow={4}
                    scrollOnDevice={true}
                >
                    </InfiniteCarousel>    */}
                    
                <a className = "CategoryCell">
               
                            <div>
                                <div>
                                            <a className="game-type-link active"  href ={'/home/' + categories.CategoryName} >
                                                <div className="game-type__name">{categories.CategoryName}</div>
                                            </a>
                                </div>
                                <div>     
                                    <a className="famous-game-link" href={'/game-detail/' + game.Url}>
                                        <img className="famous-game__img" src={gameAvaUrl} alt="Cannot get image" />
                                        <div className="famous-game__name">{game.Title}</div>
                                        <div className="famous-game__dev">Arcade games</div>
                                    </a>
                                </div>
                            </div>
                        );
                </a>
        </div>
    );
};

export default GameCategories;
