import React from "react";
// import axios from "axios";
import './user-profile.css'
import ChangePassword from './change-password.jsx'

import {
  Button,
  Card,
  CardHeader,
  CardFooter,
  Row,
  Col,
} from "reactstrap";
import { Component } from "react";

class UserProfile extends Component{
  constructor(props){
    super(props);
    this.state = {
      isDisplayFormChangePass: false
      // users= []

    }
  }
  // componentDidMount() {
  //   axios.get('/users/')
  //        .then(res => {
  //         this.setState({
  //           users: res.data,
  //       });
  //         })
  //        .catch(error => console.log(error));
  // };

  OpenForm = () =>{
      this.setState({
        isDisplayFormChangePass : !this.state.isDisplayFormChangePass
      });
  }
  onCloseForm =() =>{
    this.setState({
      isDisplayFormChangePass : !this.state.isDisplayFormChangePass
    });
  }
  
  render(){
    var {isDisplayFormChangePass} = this.state;
    var show = isDisplayFormChangePass ? <ChangePassword onCloseForm={this.onCloseForm} /> : '';
    return (
      <>
        <div className="content">
          <div>
            {show}
          </div>
          <Row>
            <Col md="8">
              <Card>
                <CardHeader>
                  <h1 className="title">Profile</h1>
                </CardHeader>
                  <div className="row Card-space" >
                  {/* this.state.users(item => (  */}
                  <div className="col-md-3 " key={item.id}>
                    <h5>Full Name:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <form>
                      <input type="text" name="fullname" className="o" value={item.Full_name} /> 
                    </form>
                  </div>
                  </div>
                  <hr />
                  <div className="row">
                  <div className="col-md-3">
                    <h5>Sex:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <input type="radio" value="Male" name="gender" className="a"  /> Male
                    <input type="radio" value="Female" name="gender" className="a" /> Female
                  </div>
                  </div>
                  <hr />
                  <div className="row">
                  <div className="col-md-3">
                    <h5>Date of birth:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <input type="date" name="bday" className="o"  value={item.DateOfBirth} />
                  </div>
                  </div>
                  <hr />
                  <div className="row ">
                  <div className="col-md-3">
                    <h5>Email:</h5>
                  </div>
                  <div className="col-md-3 text-secondary">
                    <form>
                      <input type="text" name="email" className="o" value={item.Email} />
                    </form>
                  </div>
                </div>
                <hr />
                ));
                <div className="row">
                  <div className="col-md-3">
                    <h5>Password:</h5>
                  </div>
                  <div className="col-md-3 text-secondary"  >
                    <p>*******************</p>
                  </div>
                </div>
                <CardFooter>
                  <Button className="btn btn-fill" >
                    Save
                  </Button>
                  <Button 
                    className="btn btn-fill"   
                    onClick={this.OpenForm}
                  >
                    Change Password
                  </Button>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}


export default UserProfile;
