    //         .then(res => {
    //             const arrListFriend = res.data.post.map((x)=>{
    //                 return x.id
    //             })
    //             const listFriendById = []
    //             arrListFriend.map((x)=>{
    //                 axios.get('user/' + x, config)
    //                 .then(res =>{
    //                     listFriendById.push(res.data)
    //                 })
    //                 .catch(err => {
    //                     console.log(err);
    //                 })
    //             })

    //             renderListFriend.current = listFriendById
