//     var dayOfBirth;
//     if(listFriend.FriendDayOfBirth != null){
//       for(let i = 0; i < 10; i++)
//         dayOfBirth += user.DayOfBirth[i];
//     }
//     return dayOfBirth;
//   }
// const showGender = () =>{
//   if(Number(listFriend.FriendGender) === NaN){
//     return document.getElementById('gender').value('Male');
//   }
//   else{
//     return document.getElementById('gender').value('Female');
//   }
// }
  // console.log(Number(listFriend.FriendGender))
  // console.log(listFriend)
    return  (user) ? (
    <>
    <div className="content">
    
      <Row>
        <Col className="12">
          <Card>
          <CardHeader>
            <h1 className="title">List friend</h1>
            
            <button 
              id="friend-list-add"
              className="btnAddFr"
              onClick={openForm}
            >      
            <i className="fas fa-user-plus"></i>
            </button>
            <div className="friend-list__add-email" id="friend-list-form">
              <input type="text" className="form-control" name='' id="add-email" placeholder="Friend Email"/>
              <button type="submit" className="btn btn-primary" onClick={AddFriend}>Add</button>
            </div>
          </CardHeader>
          <CardBody>
            <Table className="tablesorter" responsive>
            <thead className="text-primary">
                      <tr>
                        <th>Avatar</th>
                        <th>Email</th>
                        <th>Full name</th>
                        <th>Sex</th>
                        <th>Day of birth</th>
                      </tr>
            </thead>
                <tbody>
                  {
                    listFriend ? listFriend.map((friend) => (
                      <tr key={friend.id}>
                      <td><img className="friend-list__title-img" src={axios.defaults.baseURL + 'uploads/images/users/' + friend.FriendAvatar} alt="" /></td>
                      <td>{friend.FriendEmail}</td>
                      <td>{friend.FriendFullName}</td>
                      <td id='gender' >{Number(friend.FriendGender)}</td>
                      <td>{friend.FriendDayOfBirth}</td>
                    </tr>
                    )) : null
                  }
                </tbody>
            </Table>
          </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
    </>
  ) : null;
}

export default ListFriend;
