import GameByCategories from '../GameByCategoriesCell/GameByCategoriesCell';
/* <!--Game Picture --> */

/* <!--Game Link --> */

function GameCategories({listCategory,listGame}){
    const GameCategories = listCategory.map(categories => {
        return (
            <GameByCategoriesCell categories={categories}/>
        );
    })



