import React from "react";
import axios from 'axios';
import './sidebar.css'

import { NavLink } from "react-router-dom";
import { Nav } from "reactstrap";
import {
  BackgroundColorContext
} from "../BackgroundColorContext";

function SideBar({routes,user}) {
  var avatarURL;
  avatarURL = axios.defaults.baseURL + 'uploads/images/users/' + user.Avatar;
  
  var showImg = (event) => {
    const config = {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    };
    var newAvatar = document.getElementById('output');
    const updateImg ={
      Avatar : newAvatar
    }
    newAvatar = document.getElementById('output');
    newAvatar.src = URL.createObjectURL(event.target.files[0]);
    axios.post('/upload/user/', config, updateImg ).then(res => {
      console.log(res)
    })
  };
  
  return(user)?(
    <BackgroundColorContext.Consumer>
      {({ color }) => (
        <div className="sidebar" data={color}>
          <div className="sidebar-wrapper"  > 
              <div className="logo">
                <form runat="server">
                  <img className="avatar-img" src={avatarURL} alt='' id="output"  width="200px" height="200px" />            
                  <input accept="image/*" className="inputImg" type='file' onChange={showImg}/>
                </form>
                <p className='username'>{user.Full_name}</p>
              </div>
            <Nav>
              {routes.map((prop) => {
                return (
                  <li 
                  >
                    <NavLink
                      to={prop.layout + prop.path + '/'+ user.id}
                      className="nav-link"
                      activeClassName="active"
                      onClick={routes.toggleSidebar}
                    >
                      <i className={prop.icon} />
                      <p>{prop.name}</p>
                    </NavLink>
                  </li>
                );
              })}
            </Nav>
          </div>
        </div>
      )}
    </BackgroundColorContext.Consumer>
  ):null;
}
export default SideBar;
