import React from "react";

import './form.css'

import useForm from "./useForm";
import validator from "./validator";

function ChangePassword({showChangePassword, setShowChangePassword, formRef, setDialogState}) {
    const {handleSubmit, handleClose, handleChange, values, errors} = useForm("ChangePassword", formRef, setShowChangePassword, validator, null, setDialogState)
    return (showChangePassword) ? (
        <form onSubmit={handleSubmit} id="popup-form fixed-top">
            <div className="auth-inner">
                <div 
                    className="close-btn" 
                    onClick={handleClose} 
                >&times;</div>
                <h3>Reset Password</h3>
                <div className="form-group">
                    <label className="form-label">New Password:</label>
                    <input type="password" className="form-control" placeholder="New password"
                        onChange={e => this.email = e.target.value}/>
                </div>

                <div className="form-group">    
                    <label className="form-label">Confirm New Password:</label>
                    <input type="password" className="form-control" placeholder="Confirm new password"
                        onChange={e => this.password = e.target.value}/>
                </div>

                <div className="form-group">
                    <button className="btn btn-primary form-btn">Reset Password</button>
                </div>
            </div>
        </form>
    ):null;
}
 
export default ChangePassword;
