import { useState, useRef } from "react";

const useFrom = (FormType) => {
    const [showLogin, showSignUp, showForgotPassword, setShowForm] = useState(false, false, false)

    var formState = {
        showLogin,
        showSignUp,
        showForgotPassword
    }

    const handlePopup = (popupForm) => {
        if (popupForm = "Login"){
            
        }
    }

    const formRef = useRef();
    const closeForm = e => {
        if (formRef.current === e.target){
            setShowLogin(false)
            setShowSignUp(false)
            setShowForgotPassword(false)
        }
    }

    const [values, setValues] = useState(() => {
        if (FormType === "Login"){
            return {
                email: "",
                password: ""
            }
        }
        if (FormType === "SignUp"){
            return {
                full_name: "",
                email: "",
                password: "",
                confirm_password: "",
                gender: ""
            }
        }

        if (FormType === "ForgotPassword"){
            return {
                email: ""
            }
        }
    })

    const handleChange = e => {
        const { name, value } = e.target
        setValues({
            ...values,
            [name]: value
        })
    }

    const handleSubmit = e => {
        e.preventDefault();
    }

    return { formState, formRef, handlePopup, closeForm, handleSubmit, handleChange, values }
}

export default useFrom;
