import { useEffect, useState } from "react";
import axios from 'axios'

const useForm = (FormType, formRef, validator, setUser, setShowForm) => {

    const default_values = () => {
        if (FormType === "Login"){
            return {
                Email: "",
                Password: ""
            }
        }
        if (FormType === "SignUp"){
            return {
                Full_name: "",
                Email: "",
                Password: "",
                Confirm_password: "",
                Gender: null
            }
        }

        if (FormType === "ForgotPassword"){
            return {
                Email: ""
            }
        }
    }

    const [values, setValues] = useState(default_values)

    const handleChange = e => {
        var { name, value } = e.target
        if (name === "Gender"){
            value = Number(value)
        }
        setValues({
            ...values,
            [name]: value
        })
    }

    const [errors, setErrors] = useState({});

    const handleSubmit = e => {
        e.preventDefault();
        
        setErrors({});
        setErrors(validator(FormType, values));
    }

    useEffect(() => {
        if (Object.keys(errors).length === 0){
            if (FormType === "Login"){
                axios.post('user/login', values).then(
                    res => {
                        if (res.data.message === 'Authentication successful!'){
                            localStorage.setItem('token', res.data.token)
                            setUser(res.data.user)
                            setShowForm(false)
                        }
                    }
                ).catch(
                    err => setErrors(validator(FormType, 'Wrong email or password!'))
                )  
            }
            if (FormType === "SignUp"){
                axios.post('user/sign-up', values).then(
                    res => {
                        console.log(res)
                        setShowForm(false)
                    }
                ).catch(
                    err => console.log(err)
                )
            }
    
            if (FormType === "ForgotPassword"){
                axios.post('user/forgot-password', values).then(
                    res => {
                        console.log(res)
                        setShowForm(false)
                    }
                ).catch(
                    err => console.log(err)
                )
            }
            setValues(default_values)
        }  
    })

    const handleClose = (e) => {
        if (formRef.current === e.target || e.target.className === "close-btn"){
            setValues(default_values)
            setErrors({})
            setShowForm(false)
        }
    }

    return { handleSubmit, handleClose, handleChange, values , errors }
}

export default useForm;
