import './friend-list.css'
import AddFriend from './add-friend.jsx'
import {
  Card,
  CardHeader,
  CardBody,
  Table,
  Row,
  Col,
} from "reactstrap";


function ListFriend({user}){
  const getFriend = () => {
    axios.get('/friend/get-by-user-id/' + user.id )
        .then(res => {
            console.log(res.data.post);
            setListFriend(res.data.post)
        })
        .catch(err => {
            console.log(err);
        })
    }
  const showFullInfomationFriend = () =>{
    axios.post('/friend', {
      UserID : user.id,

      FriendEmail : inputEmail.value
      }, config)
      .then(response => {
          getFriend()
      })
  }
    return (
    <>
    <div className="content">
      <Row>
        <Col className="12">
          <Card>
          <CardHeader>
            <h1 className="title">List friend</h1>
            <button 
              className="btnAddFr"
            >      
            <i className="fas fa-user-plus"></i>
            </button>
          </CardHeader>
          <CardBody>
            <Table className="tablesorter" responsive>
            <thead className="text-primary">
                      <tr>
                        <th>Name</th>
                        <th>Full Name</th>
                        <th>Sex</th>
                        <th>Day of birth</th>
                        <th>Email</th>
                      </tr>
            </thead>
                    <tbody>
                      <tr>
                        <td>Nhat</td>
                        <td>NhatproNo1</td>
                        <td>Male</td>
                        <td>10/10/2001</td>
                        <td>Nhat@gmail.com</td>
                      </tr>
                    </tbody>
            </Table>
          </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
    </>
  );
}

export default ListFriend;
