import React, {useState,useEffect} from "react";
import './friend-list.css'
//import AddFriend from './add-friend.jsx'
import axios from 'axios'
import {
  Card,
  CardHeader,
  CardBody,
  Table,
  Row,
  Col,
} from "reactstrap";


function ListFriend({user}){
  const addmail = document.getElementById('add-email');
  const [listFriend, setListFriend] = useState([]);
  const getFriendbyID = () => {
    axios.get('/friend/get-by-user-id/' + user.id )
        .then(res => {
            console.log(res.data.post);
            // setListFriend(res.data.post)
            res.data.post.map((friend) => {
              getUserByID(friend.FriendID)
            })
        })
        .catch(err => {
            console.log(err);
        })
    }
    useEffect(() => {
      if(user!=null)
      getFriendbyID()
     
  },[user])
  console.log(listFriend);
  const config = {
  headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
  };
  const AddFriend = () => {
    if(addmail.value !=null)
  {
      if(user.Email != addmail.value)
      {
          axios.post('/friend', {
              UserID : user.id,
              FriendEmail : addmail.value
              }, config)
              .then(response => {
                  getFriendbyID()
              })
              .catch(err => {
                  alert('Friend exists or cant find')
          })
      }
      else alert('Can not add your self')
  }
}
  

  const getUserByID = (id) =>{
    axios.get('user/' + id,config)
      .then(res =>{
        console.log(res.data);
      })
      .catch(error =>{
        console.log(error);
      })
  }
  const [friend,showFriend] = useState([]);
  const showAllUser = () =>{
    axios.get('/get-list-friend/'+user.id,config).then(res => {
      console.log(res)
    })
    .catch(err =>{
      console.log(err);
    })
  }
  
    return  (user) ? (
    <>
    <div className="content">
    
      <Row>
        <Col className="12">
          <Card>
          <CardHeader>
            <h1 className="title">List friend</h1>
            <input type="text" id='add-email'/>
            <button 
              className="btnAddFr"
              onClick={AddFriend}
            >      
            <i className="fas fa-user-plus"></i>
            </button>
          </CardHeader>
          <CardBody>
            <Table className="tablesorter" responsive>
            <thead className="text-primary">
                      <tr>
                        <th>Email</th>
                        <th>Full name</th>
                        <th>Sex</th>
                        <th>Date of birth</th>
                      </tr>
            </thead>
                <tbody>
                  {
                    friend ? friend.map((a) => (
                      <tr key={a.id}>
                      <td>{a.Full_name}</td>
                      <td>{a.Gender}</td>
                      <td>{a.DayOfBirth}</td>
                      <td>{a.Email}</td>
                    </tr>
                    )) : null
                  }
                </tbody>
            </Table>
          </CardBody>
          </Card>
        </Col>
      </Row>
    </div>
    </>
  ) : null;
}

export default ListFriend;
