import React, { useEffect, useState } from "react";
import axios from "axios";
import "./CategoriesCell.css"
import "../GameCell/game-cell"

function CategoriesCell({categories}){
    
    const [listGame,setListGame] = useState([]) ;

    useEffect(() => {
      axios.get('game/').then(result => {
        setListGame(result.data)
      }) .catch(err => {
        console.log(err)
      })
    }, [])  

    const allgame= listGame.map(game => {})

    
    const gameAvaUrl = axios.defaults.baseURL + 'uploads/images/games/avatar/' + allgame.Avatar;
    
    return (
        <div>
            <div>
                        <a className="game-type-link"  href ={'/home/' + categories.CategoryName} >
                            <div className="game-type__name">{categories.CategoryName}</div>
                        </a>
            </div>
            
        </div>
    );
    if(allgame.Category==categories.CategoryName)
    {
        return(
            <div>     
                <a className="famous-game-link" href={'/game-detail/' + allgame.Url}>
                    <img className="famous-game__img"  src={gameAvaUrl} alt="Cannot get image" />
                    <div className="famous-game__name" >{allgame.Title}</div>
                    <div className="famous-game__dev" >Arcade games</div>
                </a>
            </div>
        );
    }
}

export default CategoriesCell;
