import React from "react";
import './form.css'

export const SignUp = ({showSignUp, setShowSignUp, setShowLogin}) => {
    const handleSubmit = e => {
        e.preventDefault();
    }; 

    return (showSignUp) ? (
        <form onSubmit={handleSubmit} className="popup-form" id="sign-up-form">
            <div className="auth-inner">

                <div class="close-btn" onClick={() => setShowSignUp(false)}>&times;</div>

                <h3>Sign Up</h3>

                <div className="form-group">
                    <label className="form-label">First Name:</label>
                    <input type="text" className="form-control" placeholder="First name"/>
                </div>

                <div className="form-group">
                    <label className="form-label">Last Name:</label>
                    <input type="text" className="form-control" placeholder="Last name"/>
                </div>

                <div className="form-group">
                    <label className="form-label">Email:</label>
                    <input type="email" className="form-control" placeholder="Email"/>
                </div>

                <div className="form-group">
                    <label className="form-label">Password:</label>
                    <input type="password" className="form-control" placeholder="Password"/>
                </div>

                <div className="form-group">
                    <label className="form-label">Confirm Password:</label>
                    <input type="password" className="form-control" placeholder="Confirm password"/>
                </div>
                
                <div className="form-group">
                    <button className="btn btn-primary btn-block">Sign Up</button>
                </div>

                <p>Already have account? 
                    <label className="form-link"
                        onClick={() => {
                            setShowSignUp(false);
                            setShowLogin(true);
                        }}>Login now</label></p>     
            </div>
        </form>
    ) : "";
}
 
export default SignUp;
