import React from "react";
import axios from "axios";
import "./CategoriesCell.css"


function CategoriesCell({categories}){

    const categoryName = document.querySelectorAll('.CategoryName');
    const AllCategoryNamePosts = document.querySelectorAll('.all');
    
    for(let i = 0; i < categoryName.length; i++){
        categoryName[i].addEventListener('click', filterPosts.bind(this, categoryName[i]));
    }
    
    function filterPosts(item){
        changeActivePosition(item);
        for(let i = 0; i < AllCategoryNamePosts.length; i++){
            if(AllCategoryNamePosts[i].classList.contains(item.attributes.id.value)){
                AllCategoryNamePosts[i].style.display = "block";
            } else {
                AllCategoryNamePosts[i].style.display = "none";
            }
        }
    }
    
    function changeActivePosition(activeItem){
        for(let i = 0; i < categoryName.length; i++){
            categoryName[i].classList.remove('active');
        }
        activeItem.classList.add('active');
    };

    return (
        <div>
            <div>
                        <a className="game-type-link active"  href ={'/home/' + categories.CategoryName} >
                            <div className="game-type__name">{categories.CategoryName}</div>
                        </a>
            </div>
            <div>
                
            </div>
        </div>
    );
}

export default CategoriesCell;
